var test = require('tape')
var rimraf = require('rimraf')
var lessCli = require('..')

test('renders css from less', function (t) {
  rimraf.sync('test/fixtures/test.css')
  t.plan(2)
  lessCli('./fixtures/', function (err, files) {
    t.error(err, 'there was not an error')
    t.ok(files, 'files exist')
    rimraf.sync('test/fixtures/test.css')
  })
})
